"use strict";function dropdown(e){document.body.clientWidth<640&&(e.currentTarget.querySelector(".countries__block").classList.toggle("active"),e.currentTarget.querySelector("header").classList.toggle("arrowUp"))}function showMenu(){document.querySelector(".navbar__menu").classList.toggle("active")}window.onload=(()=>{const e=new IntersectionObserver((e,r)=>{e.some(e=>e.isIntersecting)&&e.forEach(e=>{e.isIntersecting&&(Array.from(e.target.querySelectorAll("[class~='hidden']")).forEach(e=>{e.classList.remove("hidden")}),r.unobserve(e.target))})},{root:null,rootMargin:"0px",threshold:.2});Array.from(document.querySelectorAll(".animate")).forEach(r=>e.observe(r))}),document.querySelectorAll(".countries__container").forEach(e=>e.addEventListener("click",e=>dropdown(e))),document.querySelector(".navbar__menu-button").addEventListener("click",()=>showMenu()),document.querySelectorAll(".navbar__menu").forEach(e=>e.addEventListener("click",()=>showMenu()));